mapper.update:
update drugs set number = number + #{newBatchDrugForm.regist_num},
  checkIn_no = #{newBatchDrugForm.checkIn_no},
  legal_date = #{newBatchDrugForm.legal_date}
 where drug_no = #{newBatchDrugForm.drug_no};

update drugs set drug_name = #{drugForm.drugNameAdd},specification = #{drugForm.drugSpeAdd},
 origin = #{drugForm.drugOriginalAdd},selling_price=#{drugForm.drugSellPriceAdd},purchase_price=#{drugForm.price},
 number = #{drugForm.number} where drug_no = #{drugForm.drugNoAdd};

mapper.select:
select * from drugs


      number>0



        and
        LEFT(create_time,10) >= #{ qryDrugForm.qryStartDate}
        and
        LEFT(create_time,10) <= #{ qryDrugForm.qryEndDate}




        and
        drug_no = #{qryDrugForm.drugNo}





        and
        drug_name like #{pattern}




        and
        specification = #{qryDrugForm.drugSpe}





        and
        origin = #{pattern}




        and
        number = #{qryDrugForm.number}




        and
        legal_date = #{qryDrugForm.legalDate}




        and
        checkIn_no = #{qryDrugForm.checkInNo}



limit #{ qryDrugForm.start},#{ qryDrugForm.pageSize}

select COUNT(*) from drugs


      number>0



        and
        LEFT(create_time,10) >= #{ qryDrugForm.qryStartDate}
        and
        LEFT(create_time,10) <= #{ qryDrugForm.qryEndDate}




        and
        drug_no = #{qryDrugForm.drugNo}





        and
        drug_name like #{pattern}




        and
        specification = #{qryDrugForm.drugSpe}





        and
        origin = #{pattern}




        and
        number = #{qryDrugForm.number}




        and
        legal_date = #{qryDrugForm.legalDate}




        and
        checkIn_no = #{qryDrugForm.checkInNo}

select sum(purchase_price*number) from drugs


      number>0



        and
        LEFT(create_time,10) >= #{ qryDrugForm.qryStartDate}
        and
        LEFT(create_time,10) <= #{ qryDrugForm.qryEndDate}




        and
        drug_no = #{qryDrugForm.drugNo}





        and
        drug_name like #{pattern}




        and
        specification = #{qryDrugForm.drugSpe}





        and
        origin = #{pattern}




        and
        number = #{qryDrugForm.number}

select b.id, a.drug_no, a.drug_name, a.specification, a.origin, a.purchase_price, a.selling_price, b.regist_num as `number`, b.create_time, b.checkIn_no, b.legal_date,b.checkIn_amount
from drugs_checkin b,drugs a


      a.drug_no = b.drug_no



        and
        LEFT(b.create_time,10) >= #{ qryDrugForm.qryStartDate}
        and
        LEFT(b.create_time,10) <= #{ qryDrugForm.qryEndDate}




        and
        a.drug_no = #{qryDrugForm.drugNo}





        and
        b.drug_name like #{pattern}




        and
        b.specification = #{qryDrugForm.drugSpe}





        and
        b.origin = #{pattern}




        and
        a.regist_num = #{qryDrugForm.number}




        and
        b.legal_date = #{qryDrugForm.legalDate}




        and
        b.checkIn_no = #{qryDrugForm.checkInNo}



limit #{ qryDrugForm.start},#{ qryDrugForm.pageSize}

select COUNT(*) from drugs_checkin a,drugs b


      a.drug_no = b.drug_no



        and
        LEFT(a.create_time,10) >= #{ qryDrugForm.qryStartDate}
        and
        LEFT(a.create_time,10) <= #{ qryDrugForm.qryEndDate}




        and
        a.drug_no = #{qryDrugForm.drugNo}





        and
        b.drug_name like #{pattern}




        and
        b.specification = #{qryDrugForm.drugSpe}





        and
        b.origin = #{pattern}




        and
        a.regist_num = #{qryDrugForm.number}




        and
        b.legal_date = #{qryDrugForm.legalDate}




        and
        b.checkIn_no = #{qryDrugForm.checkInNo}
